mapper.select:
SELECT count(*) > 0
FROM melody_user
WHERE login_id = #{loginId}

SELECT count(*) > 0
FROM melody_user
WHERE nickname = #{nickname}

SELECT id, login_id, username, user_passwd, nickname, email, birth_date, join_date, membership_level
FROM melody_user
WHERE login_id = #{loginId}

SELECT id, login_id, username, user_passwd, nickname, email, birth_date, join_date, membership_level
FROM melody_user
WHERE id = #{id}

mapper.update:
UPDATE melody_user
SET user_passwd = #{password}
WHERE id = #{id}

UPDATE melody_user
nickname = #{updateUserDto.nickname}, email = #{updateUserDto.email},
WHERE id = #{id}
