mapper.select:
SELECT count(*) > 0
FROM melody_user
WHERE login_id = #{loginId}

SELECT count(*) > 0
FROM melody_user
WHERE nickname = #{nickname}

SELECT user_id, login_id, username, user_passwd, nickname, email, birth_date, join_date, membership_level
FROM melody_user
WHERE login_id = #{loginId}

SELECT user_id, login_id, username, user_passwd, nickname, email, birth_date, join_date, membership_level
FROM melody_user
WHERE user_id = #{userId}

mapper.update:
UPDATE melody_user
SET user_passwd = #{newPassword}
WHERE user_id = #{userId}

UPDATE melody_user
SET nickname = #{nickname}
WHERE user_id = #{userId}

UPDATE melody_user
SET email = #{email}
WHERE user_id = #{userId}
