mapper.select:
SELECT count(*) > 0
FROM melody_user
WHERE login_id = #{loginId}

SELECT count(*) > 0
FROM melody_user
WHERE nickname = #{nickname}

SELECT user_id, login_id, username, user_passwd, nickname, email, birth_date, join_date, membership_level
FROM melody_user
WHERE login_id = #{loginId}

SELECT user_id, login_id, username, user_passwd, nickname, email, birth_date, join_date, membership_level
FROM melody_user
WHERE user_id = #{userId}
